svg.script:
 		
 		//Javascript
 		
 		var svgns="http://www.w3.org/2000/svg";
 		var xlinkns="http://www.w3.org/1999/xlink";
 		var isRaining = false;
 		var isSunny = false;
 		var isSnowing = false;
 		var isMoon = false;
 	
 		function init(evt)
 		{
 			//Save the memory space
 			if(window.svgDocument == null)
 			{
 				svgDocument=evt.target.ownerDocument;
 			}
 			
 			showWeather();
 			
 			//Add the colorful ball on the antenna
 			var cirElement = svgDocument.createElementNS(svgns, 'circle');
 			cirElement.setAttributeNS(null, 'cx', '1060px');
 			cirElement.setAttributeNS(null, 'cy', '190px');
 			cirElement.setAttributeNS(null, 'r', '20px');
 			cirElement.setAttributeNS(null, 'fill', 'red');
 			cirElement.setAttributeNS(null, 'stroke', 'black');
 			cirElement.setAttributeNS(null, 'stroke-width', '1px');
 			
 			//Choose some random colors for it to cycle through
 			var an = svgDocument.createElementNS(svgns, 'animateColor');
 			an.setAttributeNS(null, 'attributeName', 'fill');
 			an.setAttributeNS(null, 'dur', '5s');
 			an.setAttributeNS(null, 'values', 'red;' + color() + ';' + color() + ';' + color() + ';' + color() + ';' + 'red');
 			an.setAttributeNS(null, 'repeatCount', 'indefinite');
 			
 			cirElement.appendChild(an)
 			$('controller').appendChild(cirElement);
 			$('controller').$$('animateColor',0).beginElement();
 		}
 		
 		function $(which)
 		{
 			return document.getElementById(which);
 		}
 		function $$(tag, index)
 		{
 			return document.getElementsByTagName(tag)[index];
 		}
 		function color()
 		{
 			//Returns a random rgb() color
 			return 'rgb(' + Math.round(Math.random()*255) + ',' + Math.round(Math.random()*255) + ',' + Math.round(Math.random()*255) + ')';
 		}
 		function sun()
 		{
 			if(isMoon)
 			{
 				removeKids($('myMoon'));
 				isMoon = false;
 			}
 			if(!isSunny)
 			{
 				//Use the sun
		 		var sun = svgDocument.createElementNS(svgns, 'use');
		 		sun.setAttributeNS(xlinkns, 'xlink:href', '#sun');
		 		sun.setAttributeNS(null, 'transform', 'translate(0,0)');
		 		
		 		//Move the sun --->
		 		var an = svgDocument.createElementNS(svgns, 'animateTransform');
		 		an.setAttributeNS(null, 'attributeName', 'transform');
		 		an.setAttributeNS(null, 'type', 'translate');
		 		an.setAttributeNS(null, 'from', '-50,100');
		 		an.setAttributeNS(null, 'to', '1280,100');
		 		an.setAttributeNS(null, 'dur', '6s');
		 		an.setAttributeNS(null, 'repeatCount', 'indefinite');
		 		an.setAttributeNS(null, 'additive', 'sum');
		 		
		 		//Rotate the sun
		 		var an2 = svgDocument.createElementNS(svgns, 'animateTransform');
		 		an2.setAttributeNS(null, 'attributeName', 'transform');
		 		an2.setAttributeNS(null, 'type', 'rotate');
		 		an2.setAttributeNS(null, 'from', '0');
		 		an2.setAttributeNS(null, 'to', '720');
		 		an2.setAttributeNS(null, 'dur', '6s');
		 		an2.setAttributeNS(null, 'repeatCount', 'indefinite');
		 		an2.setAttributeNS(null, 'additive', 'sum');
		 		
		 		sun.appendChild(an);
		 		sun.appendChild(an2);
		 		$('mySun').appendChild(sun);
		 		
		 		isSunny = true;
	 		}
	 		else
	 		{
	 			removeKids($('mySun'));
	 			isSunny = false;
	 		}
	 		showWeather();
 		}
 		function rain()
 		{
 			if(!isRaining)
 			{
	 			for(var i = 0; i < 20; i++)
	 			{
	 				//Create random numbers for x, y, and dur
		 			var randX = Math.round(Math.random()*1400)-200;
		 			var randY = Math.round(Math.random()*350)+400;
		 			var randDur = Math.random()*4 + .25;
		 			
		 			var rainDrop = svgDocument.createElementNS(svgns, 'use');
		 			rainDrop.setAttributeNS(xlinkns, 'xlink:href', '#raindrop');
		 			
		 			//Animate the raindrops to make them look like rain
		 			var an = svgDocument.createElementNS(svgns, 'animateTransform');
		 			an.setAttributeNS(null, 'attributeName', 'transform');
		 			an.setAttributeNS(null, 'type', 'translate');
		 			an.setAttributeNS(null, 'from', randX + ',10');
		 			an.setAttributeNS(null, 'to', randX + ',' + randY);
		 			an.setAttributeNS(null, 'dur', randDur + 's');
		 			//an.setAttributeNS(null, 'begin','0s');
		 			an.setAttributeNS(null, 'repeatCount', 'indefinite');
		 			an.setAttributeNS(null, 'additive', 'sum');
		 			
		 			rainDrop.appendChild(an);
		 			$('myRain').appendChild(rainDrop);
		 			//$('myRain').$$('animateTransform', $('weather').document.getElementsByTagName('animateTransform').length-1).beginElement();
	 			}
	 			isRaining = true;
	 		}
	 		else
	 		{
	 			//Remove the rain
	 			removeKids($('myRain'));
	 			isRaining = false;
	 		}
	 		showWeather();
 		}
 		function moon()
 		{
 			if(isSunny)
 			{
 				removeKids($('mySun'));
 				isSunny = false;
 			}
 			if(!isMoon)
 			{
 				//Add the moon
		 		var moon = svgDocument.createElementNS(svgns, 'use');
		 		moon.setAttributeNS(xlinkns, 'xlink:href', '#moon');
		 		moon.setAttributeNS(null, 'id', 'theMoon');
	  			moon.setAttributeNS(null, 'transform', 'translate(50,-100)');
		 		
		 		//Drop the moon down from the top of the screen
		 		var an = svgDocument.createElementNS(svgns, 'animateTransform');
		 		an.setAttributeNS(null, 'attributeName', 'transform');
		 		an.setAttributeNS(null, 'type', 'translate');
		 		an.setAttributeNS(null, 'from', '50,-100');
		 		an.setAttributeNS(null, 'to', '50,50');
		 		an.setAttributeNS(null, 'dur', '1s');
		 		an.setAttributeNS(null, 'fill', 'freeze');
		 		an.setAttributeNS(null, 'restart', 'never');
		 		an.setAttributeNS(null, 'id', 'slide');
		 		
		 		//Make the background dark blue
		 		var an2 = svgDocument.createElementNS(svgns, 'animate')
		 		an2.setAttributeNS(xlinkns, 'xlink:href' , '#nightBG');
		 		an2.setAttributeNS(null, 'attributeName' , 'opacity');
		 		an2.setAttributeNS(null, 'from' , '0');
		 		an2.setAttributeNS(null, 'to' , '1');
		 		an2.setAttributeNS(null, 'fill' , 'freeze');
		 		an2.setAttributeNS(null, 'begin' , 'slide.end');
		 		an2.setAttributeNS(null, 'dur' , '1.5s');
		 		
		 		//Makes the grass darker
		 		var an3 = svgDocument.createElementNS(svgns, 'animate')
		 		an3.setAttributeNS(xlinkns, 'xlink:href' , '#grass');
		 		an3.setAttributeNS(null, 'attributeName' , 'fill');
		 		an3.setAttributeNS(null, 'from' , '#00bb33');
		 		an3.setAttributeNS(null, 'to' , '#006600');
		 		an3.setAttributeNS(null, 'fill' , 'freeze');
		 		an3.setAttributeNS(null, 'begin' , 'slide.end');
		 		an3.setAttributeNS(null, 'dur' , '1.5s');
		 		
		 		//Add all these elements to the page and start the animation.
		 		moon.appendChild(an);
		 		$('myMoon').appendChild(moon);
		 		$('myMoon').appendChild(an2);
		 		$('myMoon').appendChild(an3);
		 		$('theMoon').firstChild.beginElement();
		 		
		 		isMoon = true;
	 		}
	 		else
	 		{
	 			removeKids($('myMoon'));
	 			isMoon = false;
	 		}
	 		showWeather();
 		}
 		function snow()
 		{
 			if(!isSnowing)
 			{
	 			for(var i = 0; i < 10; i++)
	 			{
	 				//Create random numbers for x, y, and dur
		 			var randX = Math.round(Math.random()*1400)-200;
		 			var randY = Math.round(Math.random()*350)+400;
		 			var randDur = Math.random()*4 + .25;
		 			//var randScale = '.' + (Math.round(Math.random()*30)+20);
		 			
		 			var flake = svgDocument.createElementNS(svgns, 'use');
		 			flake.setAttributeNS(xlinkns, 'xlink:href', '#smallFlake');
		 			//flake.setAttributeNS(null, 'transform', 'scale(' + randScale + ')');
		 			
		 			//Animate the snow to make them look like its snowing
		 			var an = svgDocument.createElementNS(svgns, 'animateTransform');
		 			an.setAttributeNS(null, 'attributeName', 'transform');
		 			an.setAttributeNS(null, 'type', 'translate');
		 			an.setAttributeNS(null, 'from', randX + ',10');
		 			an.setAttributeNS(null, 'to', randX + ',' + randY);
		 			an.setAttributeNS(null, 'dur', randDur + 's');
		 			an.setAttributeNS(null, 'repeatCount', 'indefinite');
		 			
		 			flake.appendChild(an);
		 			$('mySnow').appendChild(flake);
		 			//$('mySnow').$$('animateTransform', $('weather').document.getElementsByTagName('animateTransform').length-1).beginElement();
	 			}
	 			for(var j = 0; j < 10; j++)
	 			{
	 				//Create random numbers for x, y, and dur
		 			var randX = Math.round(Math.random()*1400)-200;
		 			var randY = Math.round(Math.random()*350)+400;
		 			var randDur = Math.random()*4 + .25;
		 			
		 			var flake = svgDocument.createElementNS(svgns, 'use');
		 			flake.setAttributeNS(xlinkns, 'xlink:href', '#smallerFlake');
		 			//flake.setAttributeNS(null, 'transform', 'scale(' + randScale + ')');
		 			
		 			//Animate the snow to make them look like its snowing
		 			var an = svgDocument.createElementNS(svgns, 'animateTransform');
		 			an.setAttributeNS(null, 'attributeName', 'transform');
		 			an.setAttributeNS(null, 'type', 'translate');
		 			an.setAttributeNS(null, 'from', randX + ',10');
		 			an.setAttributeNS(null, 'to', randX + ',' + randY);
		 			an.setAttributeNS(null, 'dur', randDur + 's');
		 			an.setAttributeNS(null, 'repeatCount', 'indefinite');
		 			
		 			flake.appendChild(an);
		 			$('mySnow').appendChild(flake);
		 			//$('mySnow').$$('animateTransform', $('weather').document.getElementsByTagName('animateTransform').length-1).beginElement();
		 			
	 			}
	 			
	 			//Makes the grass white with snow
		 		var an3 = svgDocument.createElementNS(svgns, 'animate')
		 		an3.setAttributeNS(xlinkns, 'xlink:href' , '#grass');
		 		an3.setAttributeNS(null, 'attributeName' , 'fill');
		 		an3.setAttributeNS(null, 'from' , '#00BB33');
		 		an3.setAttributeNS(null, 'to' , '#FFFFFF');
		 		an3.setAttributeNS(null, 'fill' , 'freeze');
		 		//an3.setAttributeNS(null, 'begin' , '1s');
		 		an3.setAttributeNS(null, 'dur' , '4s');
		 		
		 		$('mySnow').appendChild(an3);
		 		$('mySnow').lastChild.beginElement();
	 			isSnowing = true;
	 		}
	 		else
	 		{
	 			//Remove the rain
	 			removeKids($('mySnow'));
	 			isSnowing = false;
	 		}
	 		showWeather();
 		}
 		function removeKids(parent)
 		{
 			//I wish I had getElementsByClassName right now....
 			for(var k = parent.childNodes.length; k > 0; k--)
	 		{
	 			parent.removeChild(parent.childNodes[k-1]);
	 		}
 		}
 		function showWeather()
 		{
 			if(isSunny)
 				$('sunnyText').setAttributeNS(null, 'fill', '#FF0');
 			else
 				$('sunnyText').setAttributeNS(null, 'fill', '#FFF');
 			if(isRaining)
 				$('rainingText').setAttributeNS(null, 'fill', '#06F');
 			else
 				$('rainingText').setAttributeNS(null, 'fill', '#FFF');
 			if(isMoon)
 				$('moonlightText').setAttributeNS(null, 'fill', '#03C');
 			else
 				$('moonlightText').setAttributeNS(null, 'fill', '#FFF');
 			if(isSnowing)
 				$('snowingText').setAttributeNS(null, 'fill', '#F90');
 			else
 				$('snowingText').setAttributeNS(null, 'fill', '#FFF');
 			if(!isSunny && !isRaining && !isMoon && !isSnowing)
 				$('calmText').setAttributeNS(null, 'fill', '#0B3');
 			else
 				$('calmText').setAttributeNS(null, 'fill', '#FFF');
 		}
 		function revert()
 		{
 			removeKids($('mySun'));
 			removeKids($('myMoon'));
 			removeKids($('myRain'));
 			removeKids($('mySnow'));
 			
 			isRaining = false;
 			isSunny = false;
 			isSnowing = false;
 			isMoon = false;
 			
 			showWeather();
 		}
 		function displayMe(which, HorS)
 		{
 			if(HorS == 'show')
 				which.getElementsByTagName('text')[0].setAttributeNS(null, 'display', 'block');
 			else if(HorS == 'hide')
 				which.getElementsByTagName('text')[0].setAttributeNS(null, 'display', 'none');
 		}
